@import url('https://unpkg.com/@primo-app/primo@1.3.64/reset.css'); 

header {
  border-bottom: var(--border);
}

.page-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 1rem;
  padding-bottom: 1rem;
}

nav {
  display: flex;
  align-items: center;
  gap: 1.5rem;

  .logo {
    font-size: 1.5rem;
    font-weight: 600;
    line-height: 1.4;
    margin-right: 1.5rem;
  }

  a.link {
    display: none;
    border-bottom: 2px solid transparent;
    transition: border-color 0.1s;

    &:hover {
      border-color: var(--color-accent, #154BF4);
    }
  }

  button#close {
    display: none;
  }
}

.call-to-action {
  display: flex;
  align-items: center;
  gap: 1.5rem;
  margin-left: auto;

  button#open {
    color: var(--color-accent);
    margin-left: auto;
  }

  a {
    display: none;
    border-bottom: 2px solid transparent;
    transition: border-color 0.1s;

    &:hover {
      border-color: var(--color-accent, #154BF4);
    }
  }
}

nav#mobile-nav {
  position: absolute;
  flex-direction: column;
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow);
  border-radius: var(--border-radius);
  left: 0.5rem;
  top: 0.5rem;
  right: 0.5rem;
  padding: 2rem;

  a.logo {
    margin-right: 0;
  }

  a.link {
    display: block;
  }

  hr {
    width: 100%;
  }

  a:last-of-type {
    color: var(--button-color, white);
    background: var(--button-background, #154BF4);
    border-radius: var(--button-border-radius, 5px);
    padding: var(--button-padding, 8px 20px);
    width: 100%;
    justify-content: center;
  }

  button#close {
    display: block;
    position: absolute;
    right: 1rem;
    top: 1rem;

    svg {
      color: var(--color-accent, #154BF4);
      width: 1.5rem;
      height: 1.5rem;
    }
  }
}

@media (min-width: 600px) {
  #open,
  #mobile-nav {
    display: none;
  }

  nav a.link {
    display: inline-block;
  }

  .call-to-action a {
    display: inline-block;
  }
}

